import { Col, Row, Affix } from "antd";
import NavibarLayout from "./NavibarLayout";
import { useRef } from "react";
import useSize from "@react-hook/size";

const CentralAppLayout = ({ children }) => {
  const target = useRef(null);
  const [naviHeight, naviWidth] = useSize(target);
  return (
    <>
      <Row gutter={10}>
        <Col xs={0} md={7} />
        <Col xs={24} md={10}>
          <div ref={target}>
            <Affix offSet={"top"}>
              <NavibarLayout />
            </Affix>
          </div>
          {children}
        </Col>
        <Col xs={0} md={7} />
      </Row>
    </>
  );
};

export default CentralAppLayout;
