import { Card, Avatar, Button, Tag } from "antd";
import { DollarCircleOutlined } from "@ant-design/icons";
import { useDispatch, useSelector } from "react-redux";
import { ADD_QUANTITY_SUCCESS, SUBSTRACT_QUANTITY_SUCCESS } from "../reducers/products";
import NumberFormat from "react-number-format";
import Strike from "react-strike";

const ProductCard = ({ id, title, cover, price, quantity, discount }) => {
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: ADD_QUANTITY_SUCCESS, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: SUBSTRACT_QUANTITY_SUCCESS, data: { id } });
  };
  return (
    <Card
      key={id}
      title={
        <>
          <h1 style={{ display: "inline" }}>{title}</h1>{" "}
          {discount && <Tag color="#f50">{discount * 100}%</Tag>}
        </>
      }
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={cover} />}
      hoverable
    >
      <Card.Meta
        description={
          <>
            {discount ? (
              <>
                <h1 style={{ color: "#FF5733", fontWeight: "bold", display: "inline" }}>
                  <>
                    $
                    <NumberFormat
                      value={price * (1 - discount)}
                      displayType={"text"}
                      thousandSeparator={true}
                    />
                  </>
                </h1>
                <>
                  <strike>
                    <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
                  </strike>
                </>
              </>
            ) : (
              <>
                <DollarCircleOutlined />
                <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
              </>
            )}
            <div style={{ padding: 10 }}>
              <Button shape="circle" size="small" onClick={onClickAdd}>
                +
              </Button>
              <NumberFormat value={quantity} displayType={"text"} thousandSeparator={true} />
              <Button shape="circle" size="small" onClick={onClickSubstract}>
                -
              </Button>
            </div>
            <hr />
            <h1>
              <NumberFormat
                value={discount ? price * (1 - discount) * quantity : price * quantity}
                displayType={"text"}
                thousandSeparator={true}
              />
            </h1>
          </>
        }
      />
    </Card>
  );
};

export default ProductCard;
