import { Card, Col, Tag, Input, Row } from "antd";
import { PlusOutlined, MinusOutlined } from "@ant-design/icons";
import { useDispatch } from "react-redux";
import { ADD_QUANTITY_SUCCESS, SUBSTRACT_QUANTITY_SUCCESS } from "../reducers/products";
import NumberFormat from "react-number-format";
import { Elements } from "react-stripe-elements";

const ProductCard = ({ id, title, cover, price, quantity, discount }) => {
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: ADD_QUANTITY_SUCCESS, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: SUBSTRACT_QUANTITY_SUCCESS, data: { id } });
  };
  return (
    <Card
      key={id}
      title={
        <>
          <h1 style={{ display: "inline" }}>{title}</h1>{" "}
          {discount && <Tag color="#f50">{discount * 100}%</Tag>}
        </>
      }
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={cover} />}
      hoverable
    >
      <Card.Meta
        description={
          <>
            <Row align={"bottom"}>
              <Col md={16} xs={16}>
                {discount ? (
                  <>
                    <Col xs={16} md={16}>
                      <h1 style={{ color: "#FF5733", fontWeight: "bold", display: "inline" }}>
                        <NumberFormat
                          value={price * (1 - discount)}
                          displayType={"text"}
                          thousandSeparator={true}
                        />
                        원
                      </h1>
                    </Col>
                    <Col xs={8} md={8} style={{ verticalAlign: "bottom", paddingTop: 15 }}>
                      <strike>
                        <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />
                        원
                      </strike>
                    </Col>
                  </>
                ) : (
                  <>
                    <h1>
                      <NumberFormat value={price} displayType={"text"} thousandSeparator={true} />원
                    </h1>
                  </>
                )}
              </Col>
              <Col md={8} xs={8} style={{ verticalAlign: "middle", paddingTop: 10 }}>
                <Input
                  style={{
                    textAlign: "center",
                    verticalAlign: "middle",
                  }}
                  value={quantity}
                  size={"small"}
                  readOnly
                  prefix={<PlusOutlined onClick={onClickAdd} />}
                  suffix={<MinusOutlined onClick={onClickSubstract} />}
                />
              </Col>
            </Row>
            <Row style={{ textAlign: "right" }}>
              <Elements>
                <h1>
                  <NumberFormat
                    value={discount ? price * (1 - discount) * quantity : price * quantity}
                    displayType={"text"}
                    thousandSeparator={true}
                  />
                  원
                </h1>
              </Elements>
            </Row>
          </>
        }
      />
    </Card>
  );
};

export default ProductCard;
