import ProductCard from "../components/ProductCard";
import { useSelector, useDispatch } from "react-redux";
import CentralAppLayout from "../components/Layout/CentralAppLayout";
import MenuNavibarLayout from "../components/Layout/MenuNavibarLayout ";
import { Col, Row, PageHeader, Card, Space, Affix } from "antd";
import Router from "next/router";
import { LOAD_PRODUCTS_REQUEST } from "../reducers/shop";

const Index = () => {
  const { products } = useSelector((state) => state.shop);

  console.log("test");

  return (
    <>
      <Card>
        <Space direction="vertical" size={"middle"}>
          <Affix offSet={"top"}>
            <MenuNavibarLayout menus={[`메인`, `요리`, `주류`]} />
          </Affix>
          <Row gutter={[16, 16]}>
            {products &&
              products.map((product) => {
                return (
                  <Col sm={12} xs={24} key={product.id}>
                    <ProductCard product={product} discount={0.1} />
                  </Col>
                );
              })}
          </Row>
        </Space>
      </Card>
    </>
  );
};

Index.getInitialProps = (content) => {
  console.log("yes");
  console.log(content);
  content.store.dispatch({
    type: LOAD_PRODUCTS_REQUEST,
  });
};

export default Index;
