import { List, Button, Modal, Avatar, Card, Empty, Checkbox } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import { ADMIN_PRODUCTS_REQUEST, DELETE_PRODUCT_REQUEST } from '../../../../reducers/shop';
import PropTypes from 'prop-types';
import { CheckCircleFilled, CheckCircleOutlined } from '@ant-design/icons';

import EditProduct from '../../../../components/Generalui/EditProduct';

const Console = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const [edit, setEdit] = useState(false);
  const [product, setProduct] = useState();

  const onToggleEdit = (item) => {
    setProduct(item);
    setEdit((prev) => !prev);
  };

  const productIdArray = [];

  const { products } = useSelector((state) => state.shop);

  return (
    <Card extra={<Checkbox />}>
      {edit && (
        <Modal
          title={'수정하기'}
          visible={edit}
          onCancel={() => {
            setEdit(false);
            setProduct(null);
          }}
          footer={null}
        >
          <EditProduct product={product} setEdit={setEdit} />
        </Modal>
      )}
      {products ? (
        <List
          itemLayout={'horizontal'}
          dataSource={products}
          renderItem={(item) => {
            return (
              <>
                <List.Item key={item.id}>
                  <Card
                    activeTabKey={'test'}
                    style={{ width: '100%' }}
                    actions={[
                      <div onClick={() => onToggleEdit(item)}>수정</div>,
                      <div
                        onClick={() => {
                          const flag = confirm(`이 제품을 정말로 삭제하시겠습니까?`);
                          if (!flag) {
                            return false;
                          } else {
                            dispatch({ type: DELETE_PRODUCT_REQUEST, id: item.id });
                          }
                        }}
                      >
                        삭제
                      </div>,
                    ]}
                    extra={
                      
                      <CheckCircleFilled />}
                    title={item.title}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          src={
                            item.Images[0] && `http://127.0.0.1:3065/uploads/${item.Images[0].url}`
                          }
                          shape={'square'}
                          size={'large'}
                        />
                      }
                      description={
                        <>
                          <p>{item.price}</p>
                        </>
                      }
                    />
                  </Card>
                </List.Item>
              </>
            );
          }}
        />
      ) : (
        <>
          <Empty />
        </>
      )}
    </Card>
  );
};

Console.protoType = {
  productList: PropTypes.object.isRequired,
};

export default Console;
