const initialState = {
  products: [
    {
      id: 1,
      title: `불닭 볶음면`,
      price: 3000,
      quantity: 1,
      cover: `https://www.samyangfoods.com/upload/product/20200309/20200309161335950019.jpg`,
      description: `맛있는 불닭 볶음면`,
    },
    {
      id: 2,
      title: `불닭 볶음면`,
      price: 3000,
      quantity: 1,
      cover: `https://www.samyangfoods.com/upload/product/20200309/20200309161335950019.jpg`,
      description: `맛있는 불닭 볶음면`,
    },
  ],
};

export const LOAD_STORE_PRODUCTS = "LOAD_STORE_PRODUCTS";

export const ADD_QUANTITY_REQUEST = "ADD_QUANTITY_REQUEST";
export const ADD_QUANTITY_SUCCESS = "ADD_QUANTITY_SUCCESS";
export const ADD_QUANTITY_FAILURE = "ADD_QUANTITY_FAILURE";

export const SUBSTRACT_QUANTITY_REQUEST = "SUBSTRACT_QUANTITY_REQUEST";
export const SUBSTRACT_QUANTITY_SUCCESS = "SUBSTRACT_QUANTITY_SUCCESS";
export const SUBSTRACT_QUANTITY_FAILURE = "SUBSTRACT_QUANTITY_FAILURE";

export default (state = initialState, action) => {
  switch (action.case) {
    case LOAD_STORE_PRODUCTS: {
      return {
        ...state,
      };
    }
    case ADD_QUANTITY_REQUEST: {
      console.log("requst!");
      return {
        ...state,
      };
    }
    case ADD_QUANTITY_SUCCESS: {
      try {
        console.log("success");
        const index = state.products.findIndex((product) => product.id === action.data.id);
        const product = state.products[index];
        const quantity = product.quantity + 1;
        state.products[index] = { ...product, quantity };
        return {
          ...state,
          products,
        };
      } catch (e) {
        console.loe(e);
      }
    }
    case ADD_QUANTITY_FAILURE: {
      return {
        ...state,
      };
    }
    case SUBSTRACT_QUANTITY_REQUEST: {
      return {
        ...state,
      };
    }
    case SUBSTRACT_QUANTITY_SUCCESS: {
      // const index = state.products.findIndex((product) => product.id === action.data.id);
      // const product = state.products[index];
      // const quantity = product.quantity - 1;
      // state.products[index] = { ...product, quantity };
      return {
        ...state,
        products,
      };
    }
    case SUBSTRACT_QUANTITY_FAILURE: {
      return {
        ...state,
      };
    }
    default: {
      return {
        ...state,
      };
    }
  }
};
