import { Input } from "antd";
import { useDispatch } from "react-redux";
import { SEARCH_PRODUCT_SUCCESS } from "../../reducers/menu";
import { useState } from "react";
import axios from "axios";

const SearchBar = ({ setToggleSearch, type }) => {
  const dispatch = useDispatch();
  const [text, setText] = useState();
  const onChangeText = (e) => setText(e.target.value);

  const onSearch = () => {
    if (type == "menu") {
      dispatch({
        type: SEARCH_PRODUCT_SUCCESS,
        data: { text },
      });
    } else if (type == "index") {
      axios
        .get(`https://dapi.kakao.com/v2/local/search/address.json?query=${fullAddress}`, {
          headers: {
            Authorization: `KakaoAK ${process.env.KAKAO_APIKEY}`,
          },
        })
        .then((res) => {
          const location = res.data.documents[0];
          console.log(location.address);
          setCoordinates([Number(location.address.x), Number(location.address.y)]);
        });
    }
    setText();
    setToggleSearch(false);
  };

  return (
    <Input.Search
      size={"large"}
      value={text}
      onChange={onChangeText}
      enterButton
      onSearch={onSearch}
    />
  );
};

export default SearchBar;
