import MenuBoardTest from "../../../components/Generalui/MenuBoardTest";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useRouter } from "next/router";
import { PAGE_CHANGE_SUCCESS } from "../../../reducers/admin";
import { LOAD_PRODUCT_REQUEST_TEST } from "../../../reducers/menu";

const Menu = () => {
  const dispatch = useDispatch();
  const { query } = useRouter();
  const [IMP, setIMP] = useState(null);

  useEffect(() => {
    dispatch({ type: PAGE_CHANGE_SUCCESS, data: "menu" });
  });

  useEffect(() => {
    const { IMP } = window;
    setIMP(IMP);
    dispatch({
      type: LOAD_PRODUCT_REQUEST_TEST,
      data: { ShopId: query.ShopId },
    });
  }, []);

  return (
    <>
      <MenuBoardTest />
    </>
  );
};

export default Menu;
