import { Input, Modal, List } from "antd";
import { useDispatch } from "react-redux";
import { SEARCH_PRODUCT_SUCCESS } from "../../reducers/menu";
import { useState } from "react";
import axios from "axios";

const SearchBar = ({ setToggleSearch, type }) => {
  const dispatch = useDispatch();
  const [text, setText] = useState();
  const [coordinates, setCoordinates] = useState();
  const [list, setList] = useState([]);
  const [visible, setVisible] = useState(false);
  const onChangeText = (e) => setText(e.target.value);

  const onClickAddress = (data) => {
    setCoordinates([Number(data.address.x), Number(data.address.y)]);
  };

  const onSearch = () => {
    <Modal title={list.length == 0 ? "오류" : "검색결과"}>
      {list.length == 0 ? (
        <div>검색결과 없음</div>
      ) : (
        <List
          dataSource={list}
          renderItem={(item) => {
            return <List.Item></List.Item>;
          }}
        />
      )}
    </Modal>;
    if (type == "menu") {
      dispatch({
        type: SEARCH_PRODUCT_SUCCESS,
        data: { text },
      });
    } else if (type == "index") {
      axios
        .get(`https://dapi.kakao.com/v2/local/search/keyword?query=${text}&size=15`, {
          headers: {
            Authorization: `KakaoAK ${process.env.KAKAO_APIKEY}`,
          },
        })
        .then((res) => {
          const location = res.data.documents;
          if (location.length == 1) {
            setCoordinates([Number(location[0].address.x), Number(location[0].address.y)]);
          } else {
            setVisible(true);
          }
          // console.log(location.address);
          // setCoordinates([Number(location.address.x), Number(location.address.y)]);
        });
    }
    setText();
    setToggleSearch(false);
  };

  return (
    <Input.Search
      size={"large"}
      value={text}
      onChange={onChangeText}
      enterButton
      onSearch={onSearch}
    />
  );
};

export default SearchBar;
