import { List, Button, Avatar } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import PropTypes from "prop-types";
import { ADMIN_PRODUCTS_REQUEST } from "../../../reducers/shop";

const Console = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch({ type: ADMIN_PRODUCTS_REQUEST });
  }, []);
  const { productList } = useSelector((state) => state.shop);
  console.log(productList[0] && productList[0].Images);
  return (
    <>
      {productList[0] && (
        <Avatar src={`http://127.0.0.1:3065/uploads/${productList[0].Images[0].url}`} />
      )}
    </>
  );
};
export default Console;
