import { Card, Input, Form, DatePicker } from "antd";
import { useInput } from "../../components/CustomHooks";
import CentralAppLayout from "../../components/CentralAppLayout";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { GUEST_REGIST_REQUEST } from "../../reducers/parking";

const ParkingRegist = () => {
  const dispatch = useDispatch();

  const [plateNumber, setPlateNumber] = useState();
  const [plateNumberError, setPlateNumberError] = useState(false);
  const [dueDate, setDueDate] = useState();

  const onChangePlateNumber = (e) => {
    setPlateNumber(e.target.value);
    const regex = /^[가-힣]{2}\d{2}[가-힣]\d{4}$/;
    const regex2 = /^\d{2,3}[가-힣]{1}\d{4}$/;
    const result = regex.test(e.target.value);
    const result2 = regex2.test(e.target.value);
    setPlateNumberError(!result && !result2);
  };

  const onChangeDueDate = (e) => {
    const [start, end] = e;
    setDueDate([new Date(start), new Date(end)]);
  };

  const onSubmit = () => {
    console.log(dueDate, plateNumber);
    // dispatch({ type: GUEST_REGIST_REQUEST, data: { plateNumber, dueDate } });
  };

  return (
    <CentralAppLayout>
      <Card title={`차량등록`} actions={[<div onClick={onSubmit}>제출</div>]}>
        <Form>
          <Form.Item name="dueDate" label="방문일자">
            <DatePicker.RangePicker value={dueDate} onChange={onChangeDueDate} />
          </Form.Item>
          <Form.Item
            name="plateNumber"
            label="차량번호"
            rules={[
              {
                required: true,
                message: "차량번호를 입력해 주세요!",
              },
            ]}
          >
            <Input
              value={plateNumber}
              onChange={onChangePlateNumber}
              placeholder="차량번호를 입력해주세요"
            />
          </Form.Item>
          {plateNumberError && <div>번호가 잘못되었어</div>}
        </Form>
      </Card>
    </CentralAppLayout>
  );
};

export default ParkingRegist;
