import { Button, Affix } from 'antd';
import { ShoppingCartOutlined } from '@ant-design/icons';
import Link from 'next/link';
import { useDispatch } from 'react-redux';
import { CLEAR_CART_REQUEST } from '../../reducers/cart';
import Draggable from 'react-draggable';
import { useState } from 'react';
import Router from 'next/router';

const AffixBottom = ({ cart, windowSize, menuNaviSize, naviSize, children }) => {
  const { windowHeight, windowWidth } = windowSize;
  const { menuNaviHeight, menuNaviWidth } = menuNaviSize;
  const dispatch = useDispatch();
  const [position, setPosition] = useState({ x: 0, y: windowHeight * 0.9 - menuNaviHeight }); // box의 포지션 값
  // 업데이트 되는 값을 set 해줌
  const trackPos = (data) => {
    setPosition({ x: data.x, y: data.y });
  };

  let amount = 0;
  cart.map((product) => (amount += product.quantity));

  const onClickClearCart = () => {
    dispatch({ type: CLEAR_CART_REQUEST });
  };

  const onClickCartList = () => {
    Router.push('/cart');
  };

  return (
    <>
      <Affix style={{ position: 'absolute' }} offsetTop={windowHeight * 0.9 - menuNaviHeight}>
        <Draggable onDrag={(e, data) => trackPos(data)}>
          <Button
            type="primary"
            size="large"
            icon={<ShoppingCartOutlined />}
            onClick={onClickCartList}
          >
            {amount}개의 상품이 담겨 있습니다.
          </Button>
        </Draggable>
      </Affix>
    </>
  );
};

export default AffixBottom;
