import React, { useEffect, useCallback } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Card, Input, Form } from 'antd';
import { useRouter } from 'next/router';
import { SELECT_PRODUCT_REQUEST } from '../../../../../reducers/shop';
import {useInput} from '../../../'

const Edit = () => {
  const dispatch = useDispatch();
  const { query } = useRouter();
  const { ProductId } = query;

  useEffect(() => {
    dispatch({ type: SELECT_PRODUCT_REQUEST, ProductId });
  }, []);

  const { product } = useSelector((state) => state.shop);
  const [title, onChangeTitle] = useInput(product.title);
  const [price, onChangePrice] = useInput(product.price);
  const [description, onChangeDecription] = useInput(product.description);
  const [fileList, setFileList] = useState();
  const [fileError, setFileError] = useState();

  const onImagesChange = ({ fileList: newFileList }) => {
    setFileList(newFileList);
    setFileError(false);
  };

  const onPreview = async (file) => {
    let src = file.url;
    if (!src) {
      src = await new Promise((resolve) => {
        const reader = new FileReader();
        reader.readAsDataURL(file.originFileObj);
        reader.onload = () => resolve(reader.result);
      });
    }
    const image = new Image();
    image.src = src;
    const imgWindow = window.open(src);
    imgWindow.document.write(image.outerHTML);
  };

  const onFinish = () => {
    const formData = new FormData();
    // formData.append('title', title);
    // formData.append('price', price);
    // formData.append('description', description);
    // fileList.forEach(({ originFileObj }) => {
    //   formData.append('files', originFileObj);
    // });
    // formData.append('userId', session.id);
    // if (fileList.length > 0) {
    //   dispatch({
    //     type: EDIT_,
    //     formData,
    //   });
    //   return router.push('/admin/product');
    // } else {
    //   return setFileError(true);
    // }
  };

  return (
    <Form encType={'multipart/form-data'} onFinish={onFinish} style={{ height: '100vh' }}>
      <Card title={<PageHeader className="site-page-header" title="상품등록" />}>
        <Space direction={'vertical'} size={'middle'} style={{ width: '100%' }}>
          <Form.Item name={'title'} rules={[{ required: true, message: '상품명을 입력해주세요' }]}>
            <Input
              name={'title'}
              prefix={<GiftOutlined />}
              size={'large'}
              onChange={onChangeTitle}
            />
          </Form.Item>
          <Form.Item
            name={'price'}
            rules={[
              { required: true, message: '가격을 입력해주세요' },
              { pattern: /^[0-9]/g, message: '숫자를 입력해주세요' },
            ]}
          >
            <Input
              name={'price'}
              prefix={<DollarOutlined />}
              size={'large'}
              value={price}
              onChange={onChangePrice}
            />
          </Form.Item>
          <Form.Item
            name={'description'}
            rules={[{ required: true, message: '상세내역을 입력해주세요' }]}
          >
            <Card title={<ProfileOutlined />}>
              <Input.TextArea
                name={'description'}
                value={description}
                prefix={<ProfileOutlined />}
                size={'large'}
                maxLength={200}
                onChange={onChangeDecription}
              />
            </Card>
          </Form.Item>
          <Card title={`image`}>
            <ImgCrop rotate aspect={4 / 3} name={'image'}>
              <Upload
                action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                listType="picture-card"
                fileList={fileList}
                onChange={onImagesChange}
                onPreview={onPreview}
              >
                {fileList.length < 5 && '+ Upload'}
              </Upload>
            </ImgCrop>
            {fileError && <div style={{ color: 'red' }}>파일을 업로드 해주세요</div>}
          </Card>
          <Button type={'primary'} htmlType={'submit'} style={{ width: '100%' }}>
            제출
          </Button>
        </Space>
      </Card>
    </Form>
  );
};

export default Edit;
