import { useState, useEffect } from 'react';
import { Button } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
// import { PAYMENT_REQUEST } from "../reducers/payment";
import axios from 'axios';

const Import = ({ amount }) => {
  const dispatch = useDispatch();

  const { session } = useSelector((state) => state.user);

  const [IMP, setIMP] = useState(null);

  useEffect(() => {
    const { IMP } = window;
    setIMP(IMP);
  }, []);
  const onClickPayment = () => {
    console.log('yes');
    alert('click!');
    if (IMP !== null) {
      IMP.init('imp37098043');
      IMP.request_pay(
        {
          pg: 'kakao', // version 1.1.0부터 지원.
          pay_method: 'card',
          merchant_uid: 'merchant_' + new Date().getTime(),
          name: '주문명:결제테스트',
          amount: amount,
          buyer_email: session.email,
          buyer_name: session.name,
          buyer_tel: '010-1234-5678',
          buyer_addr: '서울특별시 강남구 삼성동',
          buyer_postcode: '123-456',
        },
        callback,
      );
    }
  };

  const callback = async (response) => {
    const { success, merchant_uid, error_msg } = response;
    if (success) {
      alert('결제 성공');
      axios
        .post('http://127.0.0.1:3065/payments/complete', { data: response })
        .then(() => {
          console.log('complete');
        })
        .catch((e) => {
          console.log(e);
        });

      // dispatch({ type: PAYMENT_REQUEST, data: response });
    } else {
      alert(`결제 실패: ${error_msg}`);
    }
  };
  return (
    <>
      <Button onClick={onClickPayment}>결제</Button>
    </>
  );
};

export default Import;
