import { Menu, Input, Modal, Popover, Space } from 'antd';
import Link from 'next/link';
import { ArrowLeftOutlined, UserOutlined } from '@ant-design/icons';
import { useSelector, useDispatch } from 'react-redux';
import Router from 'next/router';
import { LOG_OUT_SUCCESS } from '../../reducers/user';
import LoginModal from '../Generalui/Login';
import SearchBarModal from '../Generalui/SearchBar';
import ChangePassword from '../Generalui/ChangePassword';
import { useState } from 'react';
import { LOG_IN_ERROR_CLEAR } from '../../../../project2/frontend/reducers/user';
import { useChecked } from '../Generalui/CustomHooks';

const NavibarLayout = () => {
  const [toggleLogin, setToggleLogin] = useState(false);
  const [toggleSearch, setToggleSearch] = useState(false);
  const [toggleChangePassword, setToggleChangePasswod] = useState(false);
  const [show, setShow] = useState(false);

  const hideHandler = () => {
    setShow(false);
  };

  const showHandler = (show) => {
    setShow({ show });
  };

  const onToggleChangePassword = () => {
    hideHandler();
    setToggleChangePasswod((prev) => !prev);
  };

  const dispatch = useDispatch();
  const onClickLogout = () => {
    dispatch({ type: LOG_OUT_SUCCESS });
  };

  const onToggleLogin = () => {
    dispatch({ type: LOG_IN_ERROR_CLEAR });
    setToggleLogin((prev) => !prev);
  };

  const onToggleSearch = () => {
    setToggleSearch((prev) => !prev);
  };

  const { isLoggedIn, session } = useSelector((state) => state.user);

  return (
    <>
      <Modal title={'Login'} visible={toggleLogin} onCancel={onToggleLogin} onfooter={null}>
        <LoginModal setToggleLogin={setToggleLogin} />
      </Modal>
      <Modal title={'Search'} visible={toggleSearch} onCancel={onToggleSearch} footer={null}>
        <SearchBarModal setToggleSearch={setToggleSearch} />
      </Modal>
      <Modal
        title={'Change Password'}
        visible={toggleChangePassword}
        onCancel={onToggleChangePassword}
        footer={null}
      >
        <ChangePassword />
      </Modal>
      <div style={{ marginTop: '10px', backGroundColor: 'white' }}>
        <Menu mode="horizontal">
          <Menu.Item>
            <ArrowLeftOutlined onClick={() => Router.back()} />
          </Menu.Item>
          {!isLoggedIn
            ? [
                <Menu.Item style={{ float: 'right' }} key={'login'}>
                  <a onClick={onToggleLogin}>로그인</a>
                </Menu.Item>,
              ]
            : [
                <Menu.Item style={{ float: 'right' }} key={'logout'}>
                  <a onClick={onClickLogout}>로그아웃</a>
                </Menu.Item>,
                <Menu.Item key={'profile'} style={{ float: 'right' }} key={'profile'}>
                  <Popover
                    trigger={'click'}
                    placement={'bottom'}
                    title={session.name}
                    visible={show}
                    onVisibleChange={showHandler}
                    content={
                      session.division === false ? (
                        <>
                          <Space direction={'vertical'}>
                            <div onClick={onToggleChangePassword}>비밀번호 변경</div>
                            <Link href="/user/history">
                              <div>구매이력</div>
                            </Link>
                          </Space>
                        </>
                      ) : (
                        <>
                          <Space direction={'vertical'}>
                            <div onClick={onToggleChangePassword}>비밀번호 변경</div>
                            <Link href="/shop/admin">
                              <div>매장관리 페이지</div>
                            </Link>
                          </Space>
                        </>
                      )
                    }
                  >
                    <UserOutlined />
                  </Popover>
                </Menu.Item>,
              ]}
          <Menu.Item key={'search'} style={{ float: 'right' }} key={'search'}>
            <Input.Search
              enterButton
              style={{ verticalAlign: 'middle' }}
              onClick={onToggleSearch}
              readOnly
            />
          </Menu.Item>
        </Menu>
      </div>
    </>
  );
};

export default NavibarLayout;
