import { Card, Col, Space, Row, Divider, Badge, Modal } from 'antd';
import NumberFormat from 'react-number-format';
import AmountToggle from './Generalui/AmountToggle';
import { useDispatch } from 'react-redux';
import { ADD_CART_ITEM_REQUEST } from '../reducers/cart';
import { INITIATE_QUANTITY_REQUEST } from '../reducers/menu';
import { useSelector } from 'react-redux';
import axios from 'axios';
import { PAYMENT_FAILURE, PAYMENT_SUCCESS } from '../reducers/payment';
import {useState} from 'react'

const ProductCard = ({ product }) => {
  const [toggleModal, setToggleModal] = useState(false)
  const onChangeToggleModal = setToggleModal
  const dispatch = useDispatch();

  const { isLoggedIn, session } = useSelector((state) => state.user);

  const onClickAddCart = () => {
    dispatch({
      type: ADD_CART_ITEM_REQUEST,
      product,
    });
    dispatch({ type: INITIATE_QUANTITY_REQUEST, data: product });
  };

  const onClickPayment = () => {
    if (isLoggedIn) {
      if (IMP !== null) {
        IMP.init('imp37098043');
        const today = Date.now();
        IMP.request_pay(
          {
            pay_method: 'card',
            name: product.title + '-' + product.quantity + '-' + product.price,
            Merchant_uid: 'uid' + today,
            amount: product.discount
              ? product.price * (1 - product.discount) * product.quantity
              : product.price * product.quantity,
            UserId: session.id,
            ProductId: product.id,
            ShopId: product.Shop.id,
          },
          callback,
        );
      }
    } else {
      setToggleModal
    }
  };

  const callback = async (response) => {
    const { success, error_msg } = response;
    if (success) {
      axios
        // .post(`${hostIp}/payments/complete/${product.id}/${product.Shop.id}/${session.id}`, { data: response })
        .post(
          `http://127.0.0.1:3065/payments/complete/${product.id}/${product.Shop.id}/${session.id}`,
          { data: response },
        )
        .then((response) => {
          dispatch({ type: PAYMENT_SUCCESS }, { data: response });
        })
        .catch((e) => {
          console.log(e);
          dispatch({ type: PAYMENT_FAILURE }, { error: e });
        });
    } else {
      alert(`결제 실패: ${error_msg}`);
    }
  };

  return (
    <>
    <Modal title={'Alert'} visible={toggleModal} onCancel={onChangeToggleModal} footer={null}>
        로그인을 하셔야합니다.
      </Modal>
    <Card
      key={product.id}
      title={
        <>
          <h1 style={{ display: 'inline' }}>{product.title}</h1>
        </>
      }
      actions={[
        <div onClick={onClickPayment}>즉시 구매</div>,
        <div onClick={onClickAddCart}>장바구니</div>,
      ]}
      cover={
        <img
          style={{ border: `1px solid #EFF2F5` }}
          src={product.Images[0] && `http://127.0.0.1:3065/uploads/${product.Images[0].url}`}
        />
      }
      hoverable
    >
      <Card.Meta
        description={
          <>
            <Row gutter={[16]} align={'middle'} justify={'center'}>
              <Col md={16} xs={16}>
                {product.discount ? (
                  <div>
                    <h1 style={{ color: '#FF5733', fontWeight: 'bold', display: 'inline-block' }}>
                      <NumberFormat
                        value={product.price * (1 - discount)}
                        displayType={'text'}
                        thousandSeparator={true}
                      />
                      원
                    </h1>
                    {'  '}
                    <h3 style={{ color: '#AA9C99', display: 'inline-block' }}>
                      {(1 - product.discount) * 100}%
                    </h3>
                  </div>
                ) : (
                  <div>
                    <h5>가격</h5>
                    <h1>
                      <NumberFormat
                        value={product.price}
                        displayType={'text'}
                        thousandSeparator={true}
                      />
                      원
                    </h1>
                  </div>
                )}{' '}
              </Col>
              <Col md={8} xs={8}>
                <AmountToggle id={product.id} quantity={product.quantity} />
              </Col>
            </Row>
            <Divider />
            <Row align={'middle'} justify={'center'}>
              <Col span={8} xs={8}>
                <div>총 주문금액</div>
              </Col>
              <Col span={16} xs={16}>
                <div style={{ textAlign: 'right' }}>
                  <h1>
                    <NumberFormat
                      value={
                        product.discount
                          ? product.price * (1 - product.discount) * product.quantity
                          : product.price * product.quantity
                      }
                      displayType={'text'}
                      thousandSeparator={true}
                    />
                    원
                  </h1>
                </div>
              </Col>
            </Row>
          </>
        }
      />
      </Card>
      </>
  );
};

export default ProductCard;
