import { List, Avatar, Card, Button, Row, Col, Divider } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { RestOutlined } from '@ant-design/icons';
import CartAmountToggle from '../../components/Generalui/CartAmoutToggle';
import NumberFormat from 'react-number-format';
import { REMOVE_ITEM_REQUEST } from '../../reducers/cart';

const Cart = () => {
  const dispatch = useDispatch();
  const { cart } = useSelector((state) => state.cart);

  const onClickRemoveItem = (id) => {
    dispatch({ type: REMOVE_ITEM_REQUEST, id });
  };

  return (
    <>
      <List
        itemLayout="horizontal"
        dataSource={cart}
        renderItem={(item) => (
          <List.Item>
            <Card
              title={<h1>{item.title}</h1>}
              style={{ width: '100%' }}
              extra={
                <h2>
                  <RestOutlined onClick={onClickRemoveItem(item.id)} />
                </h2>
              }
            >
              <Card.Meta
                description={
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        size={100}
                        shape="square"
                        src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                      />
                    }
                    description={
                      <Row Gutter={[8, 16]}>
                        <Col span={24}>{item.description}</Col>
                        <Col span={16}>
                          가격:{' '}
                          <NumberFormat
                            value={item.price}
                            displayType={'text'}
                            thousandSeparator={true}
                          />
                        </Col>
                        <Col span={8}>
                          <CartAmountToggle id={item.id} quantity={item.quantity} />
                        </Col>
                      </Row>
                    }
                  />
                }
              />
              <Divider />
              <Row gutter={[8, 16]}>
                <Col style={{ textAlign: 'right' }} span={24}>
                  총액:{' '}
                  <NumberFormat
                    value={item.price * item.quantity}
                    displayType={'text'}
                    thousandSeparator={true}
                  />
                </Col>
              </Row>
            </Card>
          </List.Item>
        )}
      />
    </>
  );
};

export default Cart;
