import {
  PlusCircleOutlined,
  MinusCircleOutlined,
  MinusOutlined,
  PlusOutlined,
} from "@ant-design/icons";
import { useDispatch } from "react-redux";
import { NEAR_ADD_QUANTITY_REQUEST, NEAR_SUBSTRACT_QUANTITY_REQUEST } from "../../reducers/shop";
import { Col, Row, Typography, Input } from "antd";
import NumberFormat from "react-number-format";

const NearAmountToggle = ({ id, quantity, price, discount }) => {
  console.log(quantity);
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      dispatch({ type: NEAR_ADD_QUANTITY_REQUEST, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: NEAR_SUBSTRACT_QUANTITY_REQUEST, data: { id } });
  };
  console.log(discount);
  return (
    <Row>
      <Col span={12}>
        <Typography.Text>
          <NumberFormat
            style={{
              verticalAlign: "middle",
            }}
            value={discount ? price * ((100 - discount.rate) * 0.01) : price}
            displayType={"text"}
            thousandSeparator={true}
          />
          Ïõê
        </Typography.Text>
      </Col>
      <Col span={12}>
        <Input
          style={{
            marginTop: "10%",
            textAlign: "center",
            verticalAlign: "middle",
            width: "100%",
          }}
          value={quantity}
          size={"small"}
          readOnly
          maxLength={1}
          addonBefore={<PlusOutlined onClick={onClickAdd} />}
          addonAfter={<MinusOutlined onClick={onClickSubstract} />}
        />
      </Col>
    </Row>
  );
};

export default NearAmountToggle;
