import { Menu, Modal, Input } from "antd";
import Link from "next/link";
import { HomeOutlined, UserOutlined } from "@ant-design/icons";
import { useSelector } from "react-redux";
const NavibarLayout = ({ children }) => {
  const { isLoggedIn } = useSelector((state) => state.user);
  return (
    <div style={{ marginTop: "10px" }}>
      <Menu mode="horizontal">
        <Menu.Item key={"home"}>
          <Link href={"/"}>
            <a>
              <HomeOutlined />
            </a>
          </Link>
        </Menu.Item>
        {isLoggedIn
          ? [
              <Menu.Item style={{ float: "right" }} key={"login"}>
                <Link href={"/login"} key={"login"}>
                  <a>로그인</a>
                </Link>
              </Menu.Item>,
            ]
          : [
              <Menu.Item style={{ float: "right" }} key={"logout"}>
                <Link href={"/logout"}>
                  <a>로그아웃</a>
                </Link>
              </Menu.Item>,
              <Menu.Item key={"profile"} style={{ float: "right" }} key={"profile"}>
                <Link href={"/profile"}>
                  <a>
                    <UserOutlined />
                  </a>
                </Link>
              </Menu.Item>,
            ]}
        <Menu.Item key={"search"} style={{ float: "right" }} key={"search"}>
          <Input.Search enterButton style={{ verticalAlign: "middle" }} />
        </Menu.Item>
      </Menu>
      {children}
    </div>
  );
};

export default NavibarLayout;
