import { useState, useEffect } from 'react';
import { Button } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
// import { PAYMENT_REQUEST } from "../reducers/payment";
import axios from 'axios';

const Import = ({ shopId, amount }) => {
  const dispatch = useDispatch();

  const { session } = useSelector((state) => state.user);

  const [IMP, setIMP] = useState(null);

  useEffect(() => {
    const { IMP } = window;
    setIMP(IMP);
  }, []);
  

  const callback = async (response) => {
    const { success, merchant_uid, error_msg } = response;
    if (success) {
      axios
        // .post(`${hostIp}/payments/complete`, { data: response })
        .post('http://127.0.0.1:3065/payments/complete', { data: response })
        .then(() => {
          dispatch({ type: PAYMENT_SUCCESS }, { data: response });
        })
        .catch((e) => {
          dispatch({ type: PAYMENT_FAILURE }, { error: error_msg });
          console.log(e);
        });
    } else {
      alert(`결제 실패: ${error_msg}`);
    }
  };
};

export default Import;
