import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  user: null,
  isLoggedIn: false,
  isSigningIn: false,
  isSignedIn: false,
  bizcodeConfirm: undefined,
  emailConfirm: undefined,
};

export const LOG_IN_REQUEST = "LOG_IN_REQUEST";
export const LOG_IN_SUCCESS = "LOG_IN_SUCCESS";
export const LOG_IN_FAILURE = "LOG_IN_FAILURE";

export const LOG_OUT_REQUEST = "LOG_OUT_REQUEST";
export const LOG_OUT_SUCCESS = "LOG_OUT_SUCCESS";
export const LOG_OUT_FAILURE = "LOG_OUT_FAILURE";

export const SIGN_UP_REQUEST = "SIGN_IN_REQUEST";
export const SIGN_UP_SUCCESS = "SIGN_IN_SUCCESS";
export const SIGN_UP_FAILURE = "SIGN_IN_FAILURE";

export const LOAD_USER_REQUEST = "LOAD_USER_REQUEST";
export const LOAD_USER_SUCCESS = "LOAD_USER_SUCCESS";
export const LOAD_USER_FAILURE = "LOAD_USER_FAILURE";

export const USER_SIGNIN_REQUEST = "USER_SIGNIN_REQUEST";
export const USER_SIGNIN_SUCCESS = "USER_SIGNIN_SUCCESS";
export const USER_SIGNIN_FAILURE = "USER_SIGNIN_FAILURE";

export const BIZCODE_CONFIRM_REQUEST = "BIZCODE_CONFIRM_REQUEST";
export const BIZCODE_CONFIRM_SUCCESS = "BIZCODE_CONFIRM_SUCCESS";
export const BIZCODE_CONFIRM_FAILURE = "BIZCODE_CONFIRM_FAILURE";

export const EMAIL_CONFIRM_REQUEST = "EMAIL_CONFIRM_REQUEST";
export const EMAIL_CONFIRM_SUCCESS = "EMAIL_CONFIRM_SUCCESS";
export const EMAIL_CONFIRM_FAILURE = "EMAIL_CONFIRM_FAILURE";

export default (state = initialState, action) => {
  switch (action.type) {
    case HYDRATE: {
      return { ...state, ...action.payload };
    }

    case LOG_IN_REQUEST: {
      return {
        ...state,
      };
    }

    case LOG_IN_SUCCESS: {
      return {
        ...state,
        user: action.data,
      };
    }

    case LOG_IN_FAILURE: {
      return {
        ...state,
      };
    }

    case LOG_OUT_REQUEST: {
      return {
        ...state,
      };
    }

    case LOG_OUT_SUCCESS: {
      return {
        ...state,
        user: null,
      };
    }

    case LOG_OUT_FAILURE: {
      return {
        ...state,
      };
    }

    case SIGN_UP_REQUEST: {
      return {
        ...state,
      };
    }

    case SIGN_UP_SUCCESS: {
      return {
        ...state,
      };
    }

    case SIGN_UP_FAILURE: {
      return {
        ...state,
      };
    }

    case LOAD_USER_REQUEST: {
      return {
        ...state,
      };
    }

    case LOAD_USER_SUCCESS: {
      return {
        ...state,
        user: action.data,
      };
    }

    case LOAD_USER_FAILURE: {
      return {
        ...state,
      };
    }

    case USER_SIGNIN_REQUEST: {
      return {
        ...state,
      };
    }

    case USER_SIGNIN_SUCCESS: {
      return {
        ...state,
        user: action.session,
      };
    }

    case USER_SIGNIN_FAILURE: {
      return {
        ...state,
      };
    }

    default: {
      return {
        ...state,
      };
    }
  }
};
