import { PlusOutlined, MinusOutlined } from "@ant-design/icons";
import { useDispatch } from "react-redux";
// import { PLUS_QUANTITY_SUCCESS, MINUS_QUANTITY_SUCCESS } from "../../reducers/stock";
import { Input } from "antd";
import { MINUS_QUANTITY_SUCCESS, PLUS_QUANTITY_SUCCESS } from "../../reducers/stock";

const AmountToggle = ({ id, quantity, stock }) => {
  const dispatch = useDispatch();
  const onClickAdd = () => {
    try {
      console.log(id);
      dispatch({ type: PLUS_QUANTITY_SUCCESS, data: { id } });
    } catch (err) {
      console.log(err);
    }
  };
  const onClickSubstract = () => {
    dispatch({ type: MINUS_QUANTITY_SUCCESS, data: { id } });
  };
  return (
    <Input
      style={{
        textAlign: "center",
        verticalAlign: "middle",
        fontSize: "50%",
      }}
      value={quantity}
      readOnly
      size={"small"}
      maxLength={2}
      addonBefore={<PlusOutlined style={{ width: "50%" }} onClick={onClickAdd} />}
      addonAfter={<MinusOutlined style={{ width: "50%" }} onClick={onClickSubstract} />}
    />
  );
};

export default AmountToggle;
