import ProductCard from "../components/ProductCard";
import { useSelectors } from "react-redux";

const Index = () => {
  const { products } = useSelectors((state) => state.products);
  return (
    <>
      {products &&
        products.map((product) => {
          return (
            <ProductCard
              title={product.title}
              price={product.price}
              quantity={product.quantity}
              cover={product.cover}
            />
          );
        })}
    </>
  );
};

export default Index;
