import { List, Button, Space, Modal, Avatar, Card, Empty, Checkbox } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import {
  ADMIN_PRODUCTS_REQUEST,
  CHECK_ALL_PRODUCTS_SUCCESS,
  CHECK_PRODUCT_SUCCESS,
  DELETE_PRODUCT_REQUEST,
} from '../../../../reducers/shop';
import PropTypes from 'prop-types';

import EditProduct from '../../../../components/Generalui/EditProduct';
import DiscountForm from '../../../../components/Generalui/DiscountForm'
import { useChecked } from '../../../../components/Generalui/CustomHooks';

const Console = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const [edit, setEdit] = useState(false);
  const [product, setProduct] = useState();
  const [] = useChecked(false)

  const onToggleEdit = (item) => {
    setProduct(item);
    setEdit((prev) => !prev);
  };

  const onClickCheckBox = ({ id }) => {
    dispatch({ type: CHECK_PRODUCT_SUCCESS, id });
  };

  const onClickCheckAllBoxs = (e) => {
    console.log(e.target.checked);
    dispatch({ type: CHECK_ALL_PRODUCTS_SUCCESS, checked: e.target.checked });
  };

  const { products } = useSelector((state) => state.shop);

  return (
    <Card
      title={
        <Space>
          <Button>선택할인</Button>
        </Space>
      }
      extra={<Checkbox onChange={onClickCheckAllBoxs}>전체선택</Checkbox>}
    >
      {edit && (
        <Modal
          title={'수정하기'}
          visible={edit}
          onCancel={() => {
            setEdit(false);
            setProduct(null);
          }}
          footer={null}
        >
          <EditProduct product={product} setEdit={setEdit} />
        </Modal>
      )}
      {products ? (
        <List
          itemLayout={'horizontal'}
          dataSource={products}
          renderItem={(item) => {
            return (
              <>
                <List.Item key={item.id}>
                  <Card
                    bodyStyle={{ backgroundColor: '#40A9FF' }}
                    style={{ width: '100%' }}
                    actions={[
                      <div onClick={() => onToggleEdit(item)}>수정</div>,
                      <div
                        onClick={() => {
                          const flag = confirm(`이 제품을 정말로 삭제하시겠습니까?`);
                          if (!flag) {
                            return false;
                          } else {
                            dispatch({ type: DELETE_PRODUCT_REQUEST, id: item.id });
                          }
                        }}
                      >
                        삭제
                      </div>,
                    ]}
                    extra={
                      <Checkbox checked={item.checked} onClick={() => onClickCheckBox(item)} />
                    }
                    title={item.title}
                  >
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          src={
                            item.Images[0] && `http://127.0.0.1:3065/uploads/${item.Images[0].url}`
                          }
                          shape={'square'}
                          size={'large'}
                        />
                      }
                      description={
                        <>
                          <p>{item.price}</p>
                        </>
                      }
                    />
                  </Card>
                </List.Item>
              </>
            );
          }}
        />
      ) : (
        <>
          <Empty />
        </>
      )}
    </Card>
  );
};

Console.protoType = {
  productList: PropTypes.object.isRequired,
};

export default Console;
