import { useDispatch, useSelector } from 'react-redux';
import { Form, Button, Input, Slider } from 'antd';
import { DISCOUNT_PRODUCT_REQUEST } from '../../reducers/shop';
// import { useInput } from '../../components/Generalui/CustomHooks';
import {useState} from 'react'

const DiscountForm = ({ products, id, setDiscount }) => {
  const dispatch = useDispatch();
  // const [rate, onChangeRate] = useInput();
  let ProductIds = [];


  const onFinish = () => {
    if (products) {
      const checkedProduct = products.filter((v) => v.checked == true);
      checkedProduct.map((v) => ProductIds.push({ ProductId: v.id }));
    } else {
      ProductIds = [{ ProductId: id }];
    }

    dispatch({
      type: DISCOUNT_PRODUCT_REQUEST,
      ProductIds,
    });
    setDiscount(false);
  };

  return (
    <>
      <Form onFinish={onFinish}>
        <Form.Item>
          <Slider value={rate} onChange={onChangeRate} />
          {/* <Input value={rate} onChange={onChangeRate} /> */}
        </Form.Item>
        <Button htmlType={'submit'} type={'primary'}>
          등록
        </Button>
      </Form>
    </>
  );
};

export default DiscountForm;
