import { LOAD_PRODUCTS_REQUEST } from '../../../../reducers/menu';
import MenuBoard from '../../../../components/Generalui/MenuBoard';
import { useEffect } from 'react';

const Menu = () => {
  useEffect(() => {
    const { IMP } = window;
    setIMP(IMP);
    dispatch({
      type: LOAD_PRODUCTS_REQUEST,
      data: { shopId: query.shopId, tableId: query.tableId },
    });
  }, []);

  return (
    <>
      <MenuBoard />
    </>
  );
};

export default Menu;
