import produce from 'immer';

const initialState = {
  isLoggedIn: false,
  isLoggingIn: false,
  loginError: null,
  isLoggeingOut: false,
  logoutError: null,
  candinates: null,
  user: null,
  isLoadingUser: false,
  loadingUserError: null,
  isLoadingCandinate: false,
  lodingCandidateError: null,
  isLoadedCandinates: false,
  isSelectingCandidate: false,
};

export const SIGN_UP_REQUEST = 'SIGN_UP_REQUEST';
export const SIGN_UP_SUCCESS = 'SIGN_UP_SUCCESS';
export const SIGN_UP_FAILURE = 'SIGN_UP_FAILURE';

export const LOAD_USER_REQUEST = 'LOAD_USER_REQUEST';
export const LOAD_USER_SUCCESS = 'LOAD_USER_SUCCESS';
export const LOAD_USER_FAILURE = 'LOAD_USER_FAILURE';

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';

export default (state = initialState, action) => {
  return produce(state, (draft) => {
    switch (action.type) {
      case LOAD_USER_REQUEST:
        draft.isLoadingUser = true;
        break;
      case LOAD_USER_SUCCESS:
        draft.isLoadingUser = false;
        break;
      case LOAD_USER_FAILURE:
        draft.isLoadingUser = false;
        draft.loadingUserError = action.error;
        break;
      case LOG_IN_REQUEST:
        draft.isLoggingIn = true;
        break;
      case LOG_IN_SUCCESS:
        draft.isLoggingIn = false;
        draft.isLoggedIn = true;
        draft.user = action.data;
        break;
      case LOG_IN_FAILURE:
        draft.isLoggingIn = false;
        draft.loginError = action.error;
        break;
      case LOAD_CANDINATES_REQUEST:
        draft.isLoadingCandinate = true;
        break;
      case LOAD_CANDINATES_SUCCESS:
        draft.isLoadingCandinate = false;
        draft.isLoadedCandinates = true;
        draft.candinates = candinates;
        break;
      case LOAD_CANDINATES_FAILURE:
        draft.isLoadingCandinate = false;
        draft.lodingCandidateError = action.error;
        break;
      default:
        state;
        break;
    }
  });
};
