import { Card, Avatar, Button, Col, Row, Descriptions } from "antd";
import { useDispatch, useSelect } from "react-redux";
import { ADD_QUANTITY_SUCCESS, ADD_QUANTITY_REQUEST } from "../reducers/products";
const ProductCard = ({ id, title, cover, price, quantity }) => {
  const dispatch = useDispatch();
  const products = useSelect((state) => state.products);
  const onClickAddButton = () => {
    try {
      dispatch({ type: ADD_QUANTITY_SUCCESS, data: { id } });
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <Card
      key={id}
      title={title}
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={cover} />}
    >
      <Card.Meta
        description={
          <Descriptions bordered>
            <Descriptions.Item label={"가격"} span={24}>
              {price}
            </Descriptions.Item>
            <Descriptions.Item label={"수량"} span={24}>
              <Button onClick={onClickAddButton}>+</Button>
              {quantity}
              <Button>-</Button>
            </Descriptions.Item>
            <Descriptions.Item label={"총액"} span={24}>
              {price * quantity}
            </Descriptions.Item>
          </Descriptions>
        }
      />
    </Card>
  );
};

export default ProductCard;
