import ProductCard from "../../../components/ProductCard";
import { useSelector } from "react-redux";
import MenuNavibarLayout from "../../../components/Layout/MenuNavibarLayout ";
import { Col, Row, Card, Space, Affix, Empty } from "antd";
import { LOAD_USER_REQUEST } from "../../../reducers/user";
import { LOAD_PRODUCTS_REQUEST } from "../../../reducers/menu";
import Link from "next/link";
import wrapper from "../../../store/configureStore";
import axios from "axios";
import { END } from "redux-saga";
import AffixBottom from "../../../components/Generalui/AffixBottom";

const Index = () => {
  const { products } = useSelector((state) => state.menu);
  const { session } = useSelector((state) => state.user);

  return (
    <>
      <Card>
                {cart[0] && <AffixBottom cart={cart} />}
        {products && products[0] ? (
          <Space direction="vertical" size={"middle"}>
            <Affix offSet={"top"}>
              <MenuNavibarLayout menus={[`메인`, `요리`, `주류`]} />
            </Affix>
            <Row gutter={[16, 16]}>
              {products &&
                products.map((product) => {
                  return (
                    <Col sm={12} xs={24} key={product.id}>
                      <ProductCard product={product} />
                    </Col>
                  );
                })}
            </Row>
          </Space>
        ) : (
          <Row justify="center" align={"middle"} style={{ height: "100vh", textAlign: "center" }}>
            {session && session.division == true ? (
              <Link href={"/shop/product/add"}>
                <a>
                  <Col>
                    <h1>메뉴정보가 존재하지 않습니다.</h1>
                    <Empty />
                  </Col>
                </a>
              </Link>
            ) : (
              <Col>
                <h1>메뉴정보가 존재하지 않습니다.</h1>
                <Empty />
              </Col>
            )}
          </Row>
        )}
      </Card>
    </>
  );
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  const cookie = context.req ? context.req.headers.cookie : "";
  axios.defaults.headers.Cookie = "";
  if (context.req && cookie) {
    axios.defaults.headers.Cookie = cookie;
  }
  context.store.dispatch({ type: LOAD_USER_REQUEST });
  const { shopId } = context.query;
  context.store.dispatch({ type: LOAD_PRODUCTS_REQUEST, data: { shopId } });
  context.store.dispatch(END);
  await context.store.sagaTask.toPromise();
});

export default Index;
