import { Card, Input, Form } from "antd";
import { useState } from "react";
import { useInput } from "../../components/customHooks";

const Join = () => {
  const [email, onChangeEmail] = useInput();
  const [password, onChangePassword] = useInput();
  const [passwordCheck, onChangePasswordCheck] = useInput();
  const [passwordConfirm, onChangePasswordConfirm] = useState(false);
  const [checkbox, onChangeCheckbox] = useState(false);
  return (
    <Form>
      <Form.Item
        name="email"
        label="E-mail"
        rules={[
          {
            type: "email",
            message: "The input is not valid E-mail!",
          },
          {
            required: true,
            message: "Please input your E-mail!",
          },
        ]}
      >
        <Input value={email} onChange={onChangeEmail} />
      </Form.Item>
      <Form.Item
        label="Password"
        name="password"
        rules={[
          {
            required: true,
            message: "Please input your password!",
          },
        ]}
      >
        <Input.Password value={password} onChange={onChangePassword} />
      </Form.Item>
      <Form.Item
        name="passwordCheck"
        label="password check"
        rules={[
          {
            required: true,
            message: "Please input your password check",
          },
        ]}
      >
        <Input.Password value={passwordCheck} onChange={onChangePasswordCheck} />
      </Form.Item>
    </Form>
  );
};

export default Join;
