import { List, Avatar, Card, Empty, Rate, DatePicker, Modal, Input, Button, Form } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useChecked, useInput } from '../../components/Generalui/CustomHooks';

import NumberFormat from 'react-number-format';

import { useEffect, useState } from 'react';
import { useWindowSize } from '@react-hook/window-size';
import axios from 'axios';
import { useRouter } from 'next/router';
import {
  CLIENT_HISTORY_REMOVE_REQUEST,
  LOAD_PAYMENT_HISTORY_REQUEST,
  RATING_REQUEST,
  SEARCH_HISTORY_REQUEST,
} from '../../reducers/payment';

const { RangePicker } = DatePicker;

const History = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const [toggleRating, onToggleRating, setToggleRating] = useChecked(false);
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  const [text, onChangeText, setSetextarch] = useInput();

  const [toggleSearch, setToggleSearch] = useState(false);

  const { isLoggedIn, session } = useSelector((state) => state.user);
  const { history } = useSelector((state) => state.payment);
  useEffect(() => {
    dispatch({ type: LOAD_PAYMENT_HISTORY_REQUEST, division: session.division, id: session.id });
  }, []);

  if (!isLoggedIn) {
    router.back();
  }

  const [windowWidth, windowHeight] = useWindowSize();

  const onWindowScroll = () => {
    window.scrollTo({ top: 0 });
  };

  const onChangeDatePicker = (e) => {
    const date1 = new Date(e[0]._d);
    const date2 = new Date(e[1]._d);
    setStartDate(date1);
    setEndDate(date2);
  };

  const onToggleSearch = () => {
    setToggleSearch((prev) => !prev);
  };

  const dateFormat = 'YYYY/MM/DD';

  const onFinishSearch = () => {
    dispatch({
      type: SEARCH_HISTORY_REQUEST,
      date: {
        startDate,
        endDate,
        text,
      },
    });
    setStartDate(null);
    setEndDate(null);
    setText(null);
    return setToggleSearch(false);
  };

  return (
    <>
      <Modal title={'Search'} visible={toggleSearch} onCancel={onToggleSearch} footer={null}>
        <Form onFinish={onFinishSearch}>
          {/* <Card> */}
          <Input.Search style={{ verticalAlign: 'middle' }} onChange={onChangeText} enterButton />
          <RangePicker
            style={{ verticalAlign: 'middle', width: '100%' }}
            format={dateFormat}
            onChange={onChangeDatePicker}
          />
          <Button style={{ width: '100%' }} htmlType={'submit'}>
            검색
          </Button>
          {/* </Card> */}
        </Form>
      </Modal>

      {history.length === 0 ? (
        <Card style={{ height: '100vh' }}>
          <div style={{ marginTop: windowHeight * 0.3, textAlign: 'center' }}>
            <Empty />
            <h1>거래내역이 존재하지 않습니다</h1>
          </div>
        </Card>
      ) : (
        <Card extra={<Button onClick={onToggleSearch}>검색</Button>}>
          <List
            itemLayout="horizontal"
            dataSource={history}
            renderItem={(item) => {
              const date = new Date(item.createdAt);
              const localeDate = date.toLocaleString();

              const onChangeRating = (value) => {
                dispatch({
                  type: RATING_REQUEST,
                  data: {
                    PaymentsId: item.id,
                    UserId: session.id,
                    rate: value,
                    ProductId: item.Product.id,
                  },
                });
              };

              const onClickRemoveHistory = () => {
                dispatch({
                  type: CLIENT_HISTORY_REMOVE_REQUEST,
                  PaymentId: item.id,
                });
              };

              return (
                <List.Item>
                  <Card
                    style={{ width: '100%' }}
                    actions={[<div onClick={onClickRemoveHistory}>결제취소</div>]}
                    extra={item.status ? '결제완료' : '취소요청'}
                  >
                    <List.Item.Meta
                      title={
                        <>
                          {item.Shop.name}_{item.Product.title}_{localeDate}
                        </>
                      }
                      description={
                        <>
                          <p>
                            결제수량:{' '}
                            <NumberFormat
                              value={item.quantity}
                              displayType={'text'}
                              thousandSeparator={true}
                            />
                            개, 결제단가:{' '}
                            <NumberFormat
                              value={item.price}
                              displayType={'text'}
                              thousandSeparator={true}
                            />
                            원
                          </p>
                          <h3>
                            결제금액:{' '}
                            <NumberFormat
                              value={item.price * item.quantity}
                              displayType={'text'}
                              thousandSeparator={true}
                            />
                            원
                          </h3>
                          <Rate
                            defaultValue={item.Rating && item.Rating.rate ? item.Rating.rate : null}
                            onChange={onChangeRating}
                          />
                        </>
                      }
                      avatar={
                        <Avatar
                          size={50}
                          shape="square"
                          src={`http://127.0.0.1:3065/uploads/${item.Product.Images[0].url}`}
                        />
                      }
                    />
                  </Card>
                </List.Item>
              );
            }}
          />
        </Card>
      )}
    </>
  );
};

export default History;
