import { Card, Button, Avatar, Form, Input, Row, Col, Checkbox } from "antd";
import { LeftOutlined } from "@ant-design/icons";
import { useState, useCallback, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useInput } from "../../components/CustomHooks";
import CentralAppLayout from "../../components/CenteralAppLayout";
import NavibarLayout from "../../components/NavibarLayout";
import { SIGN_UP_REQUEST } from "../../reducers/user";
import Router, { withRouter } from "next/router";

const Signup = () => {
  const [email, onChangeEmail] = useInput();
  const [password, onChangePassword] = useInput();
  const [passwordCheck, setPasswordCheck] = useState();
  const [passwordError, setPasswordError] = useState(false);
  const [termToggle, setTermToggle] = useState(false);
  const dispatch = useDispatch();

  const onChangePasswordCheck = (e) => {
    setPasswordCheck(e.target.value);
    setPasswordError(password != e.target.value);
  };

  const onClickTermToggle = () => {
    return setTermToggle(!termToggle);
  };

  const onSubmitForm = useCallback(
    (e) => {
      if (passwordError) {
        return setPasswordError(true);
      } else {
        dispatch({
          type: SIGN_UP_REQUEST,
          data: {
            username: email,
            password: password,
          },
        });
      }
    },
    [email, password, passwordCheck]
  );
  return (
    <NavibarLayout>
      <CentralAppLayout>
        <Form onFinish={onSubmitForm}>
          <Card
            title={
              <div onClick={() => Router.back()}>
                <a>
                  <LeftOutlined />
                </a>
              </div>
            }
            style={{ height: "100%" }}
            actions={[]}
          >
            <div>
              <Checkbox onChange={onClickTermToggle}>전체동의</Checkbox>
            </div>
            {!termToggle && (
              <Col>
                <Row gutter={16}>
                  <Col span={20}>test</Col>
                  <Col span={4}>test</Col>
                  <Col span={20}>test</Col>
                  <Col span={4}>test</Col>
                </Row>
              </Col>
            )}
            <Form.Item
              name="email"
              label="Email"
              rules={[
                {
                  type: "email",
                  message: "올바른 이메일 형식을 입력해주세요!",
                },
                {
                  required: true,
                  message: "이메일를 입력해 주세요!",
                },
              ]}
            >
              <Input value={email} onChange={onChangeEmail} placeholder="Email을 입력해주세요" />
            </Form.Item>
            <Form.Item
              label="Password"
              name="password"
              rules={[
                {
                  required: true,
                  message: "비밀번호를 확인해주세요",
                },
              ]}
            >
              <Input.Password
                value={password}
                onChange={onChangePassword}
                placeholder="비밀번호를 입력해주세요"
              />
            </Form.Item>
            <Form.Item
              label="Password check"
              name="passwordCheck"
              rules={[
                {
                  required: true,
                  message: "비밀번호를 확인해 주세요",
                },
              ]}
            >
              <Input.Password
                value={passwordCheck}
                onChange={onChangePasswordCheck}
                placeholder="다시 비밀번호를 입력해주세요"
              />
            </Form.Item>
            {passwordError && <div>비밀번호가 일치하지 않습니다</div>}
            <Button htmlType={"submit"} type={"primary"} style={{ width: "100%" }}>
              회원가입
            </Button>
          </Card>
        </Form>
      </CentralAppLayout>
    </NavibarLayout>
  );
};

export default withRouter(Signup);
