import { Button, Affix } from "antd";
import { ShoppingCartOutlined } from "@ant-design/icons";
import Link from "next/link";

const AffixBottom = ({ cart, windowSize, menuNaviSize }) => {
  console.log(windowSize, menuNaviSize);
  return (
    <>
      <Link href={"/cart"}>
        <a>
          <Affix offsetBottom={windowSize.windowHeight - menuNaviSize.naviHeight}>
            <Button type="primary" size="large" icon={<ShoppingCartOutlined />}>
              {cart.length}개의 상품이 담겨 있습니다.
            </Button>
          </Affix>
        </a>
      </Link>
    </>
  );
};

export default AffixBottom;
