import { Card, Avatar, Button, Col, Row, Descriptions } from "antd";
import { useDispatch } from "react-redux";
const ProductCard = ({ title, cover, price, quantity }) => {
  const onClickAddButton = () => {};
  return (
    <Card
      title={title}
      actions={[<div>즉시 구매</div>, <div>장바구니</div>]}
      cover={<img src={cover} />}
    >
      <Card.Meta
        description={
          <Descriptions bordered>
            <Descriptions.Item label={"가격"} span={24}>
              {price}
            </Descriptions.Item>
            <Descriptions.Item label={"수량"} span={24}>
              <Button>+</Button>
              {quantity}
              <Button>-</Button>
            </Descriptions.Item>
            <Descriptions.Item label={"총액"} span={24}>
              {price * quantity}
            </Descriptions.Item>
          </Descriptions>
        }
      />
    </Card>
  );
};

export default ProductCard;
