import { List, Button, Modal, Avatar, Card } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import { ADMIN_PRODUCTS_REQUEST, DELETE_PRODUCT_REQUEST } from '../../../../reducers/shop';
import PropTypes from 'prop-types';
import Link from 'next/link';
import { useChecked } from '../../../../components/Generalui/CustomHooks';
import EditProduct from '../../../../components/Generalui/EditProduct';

const Console = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const [edit, onToggleEdit, setEdit] = useChecked(false);

  const { products } = useSelector((state) => state.shop);

