import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useRouter } from 'next/router';

import { Empty, Card, Space, List, Button, Avatar, Modal } from 'antd';
import { ADMIN_PRODUCTS_REQUEST } from '../../../reducers/shop';

import CategoryForm from '../../../components/Generalui/CategoryForm';

const Index = () => {
  const dispatch = useDispatch();
  const { session, division } = useSelector((state) => state.user);

  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  const { products, categories } = useSelector((state) => state.shop);

  const router = useRouter();

  if (division === false || session === undefined) {
    router.back();
  }

  const [category, setCategory] = useState(false);

  const onToggleCategory = (item) => {
    setCategory((prev) => !prev);
  };

  return (
    <>
      
      <Space direction={'vertical'} style={{ width: '100%' }}>
      {category && (
        <Modal
          title={'Discount'}
          visible={category}
          onCancel={() => {
            setCategory(false);
          }}
          footer={null}
        >
          <DiscountForm
            products={products}
            product={product}
            setProduct={setProduct}
            setDiscount={setDiscount}
          />
        </Modal>
      )}
        <Card
          title={'카테고리 관리'}
          extra={
            categories && categories.length < 5 ? (
              <Button onClick={() => onToggleCategory()}>카테고리 추가</Button>
            ) : (
              <div>카테고리는 최대 5개까지 설정하실 수 있습니다.</div>
            )
          }
        >
          {categories.length > 0 ? (
            <List
              dataSource={categories}
              renderItem={(item, i) => (
                <List.Item
                  key={i}
                  actions={[<div onClick={() => onToggleCategory()}>수정</div>, <div>삭제</div>]}
                >
                  <List.Item.Meta avatar={<Avatar>{i + 1}</Avatar>} title={item.name} />
                </List.Item>
              )}
            />
          ) : (
            <Empty>카테고리를 추가해주세요.</Empty>
          )}
        </Card>
        {products.length > 0 && (
          <Card
            title={'상품정보'}
            cover={<img src={`http://127.0.0.1:3065/uploads/${products[0].Images[0].url}`} />}
            onClick={() => router.push('/shop/admin/product')}
          >
            <Card.Meta
              description={
                products.length > 1
                  ? `${products[0].title} 외 ${products.length - 1}개의 상품이 존재하고 있습니다.`
                  : `${products[0].title} 상품이 존재하고 있습니다.`
              }
            />
          </Card>
        )}
      </Space>
    </>
  );
};

export default Index;
