import { Card, Carousel, Image } from 'antd';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import OneSignal from 'react-onesignal';
import axios from 'axios';
import { useRouter } from 'next/router';
import { LOAD_EVENTS_REQUEST } from '../reducers/admin';

const Index = () => {
  axios.defaults.baseURL = `${process.env.BACKEND_IP}`;

  const dispatch = useDispatch();
  const router = useRouter();

  useEffect(() => {
    dispatch({ type: LOAD_EVENTS_REQUEST });
  });

  useEffect(() => {
    OneSignal.init({ appId: 'e403941a-d352-4195-a086-502cb595a147' });
  }, []);

  const [initialized, setInitialized] = useState(false);
  OneSignal.init({ appId: 'e403941a-d352-4195-a086-502cb595a147' }).then(() => {
    setInitialized(true);
    OneSignal.showSlidedownPrompt().then(() => {
      console.log('success');
    });
  });

  const { events } = useSelector((state) => state.admin);

  return (
    <>
      <Card>
        <Card.Meta
          description={
            <div style={{ height: '100vh', width: '100%' }}>
              <Carousel autoplay dots={false}>
                {events.map((event) => (
                  <Image
                    onClick={router.push(`/event/${event.id}`)}
                    style={{ height: '100vh', width: '100%' }}
                    src={`${process.env.BACKEND_IP}/uploads/${event.Images[0].url}`}
                  />
                ))}
              </Carousel>
            </div>
          }
        />
      </Card>
    </>
  );
};

export default Index;
