import produce from 'immer';

const initialState = {
  isLoggedIn: false,
  isLoggingIn: false,
  loginError: null,
  isLoggeingOut: false,
  logoutError: null,
  candinates: null,
  user: null,
  isLoadingUser: false,
  loadingUserError: null,
  isLoadingCandinate: false,
  lodingCandidateError: null,
  isLoadedCandinates: false,
  isSelectingCandidate: false,
};

export const LOG_IN_REQUEST = 'LOG_IN_REQUEST';
export const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
export const LOG_IN_FAILURE = 'LOG_IN_FAILURE';

export const LOG_OUT_REQUEST = 'LOG_OUT_REQUEST';
export const LOG_OUT_SUCCESS = 'LOG_OUT_SUCCESS';
export const LOG_OUT_FAILURE = 'LOG_OUT_FAILURE';

export const SIGN_UP_REQUEST = 'SIGN_IN_REQUEST';
export const SIGN_UP_SUCCESS = 'SIGN_IN_SUCCESS';
export const SIGN_UP_FAILURE = 'SIGN_IN_FAILURE';

export const LOAD_USER_REQUEST = 'LOAD_USER_REQUEST';
export const LOAD_USER_SUCCESS = 'LOAD_USER_SUCCESS';
export const LOAD_USER_FAILURE = 'LOAD_USER_FAILURE';

export const BIZCODE_CONFIRM_REQUEST = 'BIZCODE_CONFIRM_REQUEST';
export const BIZCODE_CONFIRM_SUCCESS = 'BIZCODE_CONFIRM_SUCCESS';
export const BIZCODE_CONFIRM_FAILURE = 'BIZCODE_CONFIRM_FAILURE';

export const EMAIL_CONFIRM_REQUEST = 'EMAIL_CONFIRM_REQUEST';
export const EMAIL_CONFIRM_SUCCESS = 'EMAIL_CONFIRM_SUCCESS';
export const EMAIL_CONFIRM_FAILURE = 'EMAIL_CONFIRM_FAILURE';

const candinates = [
  {
    id: 1,
    Profile: {
      name: 'ws',
      Images: [
        {
          src:
            'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
        },
        {
          src:
            'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
        },
        {
          src:
            'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
        },
      ],
      description: '잘생긴 정우성',
      height: 187,
      job: '직장인',
      birth: '1991-03-27',
      address: '경기도 성남시 분당구 황새울로 196',
      Tags: [{ name: '회사원' }, { name: '영화' }],
    },
    Points: { amount: 99 },
  },
  {
    id: 2,
    Profile: {
      name: 'sm',
      Images: [
        {
          src:
            'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
        },
        {
          src:
            'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
        },
        {
          src:
            'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
        },
      ],
      description: '잘생긴 정우성',
      height: 178,
      job: '직장인',
      birth: '1987-01-03',
      address: '경기도 성남시 분당구 황새울로 196',
      Tags: [{ name: '회사원' }, { name: '영화' }],
    },
    Points: { amount: 6 },
  },
];

const user = {
  Profile: {
    name: 'sm',
    Images: [
      {
        src:
          'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
      },
      {
        src:
          'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
      },
      {
        src:
          'https://www.smlounge.co.kr/upload/woman/article/201903/thumb/41467-359248-sampleM.jpg',
      },
    ],
    description: '잘생긴 정우성',
    height: 178,
    job: '직장인',
    Tags: [{ name: '회사원' }, { name: '영화' }],
  },
};

export default (state = initialState, action) => {
  return produce(state, (draft) => {
    switch (action.type) {
      case LOAD_USER_REQUEST:
        draft.isLoadingUser = true;
        break;
      case LOAD_USER_SUCCESS:
        draft.isLoadingUser = false;
        draft.user = candinates[0];
        //draft.user = action.data.user
        break;
      case LOAD_USER_FAILURE:
        draft.isLoadingUser = false;
        draft.loadingUserError = action.error;
        break;
      case LOG_IN_REQUEST:
        draft.isLoggingIn = true;
        break;
      case LOG_IN_SUCCESS:
        draft.isLoggingIn = false;
        draft.isLoggedIn = true;
        draft.user = candinates[0];
        //draft.user = action.data.user
        break;
      case LOG_IN_FAILURE:
        draft.isLoggingIn = false;
        draft.loginError = action.error;
        break;
      case LOAD_CANDINATES_REQUEST:
        draft.isLoadingCandinate = true;
        break;
      case LOAD_CANDINATES_SUCCESS:
        draft.isLoadingCandinate = false;
        draft.isLoadedCandinates = true;
        draft.candinates = candinates;
        break;
      case LOAD_CANDINATES_FAILURE:
        draft.isLoadingCandinate = false;
        draft.lodingCandidateError = action.error;
        break;
      default:
        state;
        break;
    }
  });
};
