const initialState = {
  user: undefined,
  isLoggedIn: false,
};

export const LOG_IN_REQUEST = "LOGIN_REQUEST";
export const LOG_IN_SUCCESS = "LOGIN_SUCCESS";
export const LOG_IN_FAILURE = "LOGIN_FAILURE";

export const SIGN_UP_REQUEST = "SIGN_IN_REQUEST";
export const SIGN_UP_SUCCESS = "SIGN_IN_SUCCESS";
export const SIGN_UP_FAILURE = "SIGN_IN_FAILURE";

export default (state = initialState, action) => {
  switch (action.type) {
    case LOG_IN_REQUEST: {
      return {
        ...state,
      };
    }

    case SIGN_UP_REQUEST: {
      console.log(action.data);
      return {
        ...state,
      };
    }

    default: {
      return {
        ...state,
      };
    }
  }
};
