import { List, Button, Modal, Avatar, Card } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/router';
import { ADMIN_PRODUCTS_REQUEST, DELETE_PRODUCT_REQUEST } from '../../../../reducers/shop';
import PropTypes from 'prop-types';
import Link from 'next/link';

const Console = () => {
  const dispatch = useDispatch();
  const router = useRouter();
  const { session } = useSelector((state) => state.user);
  if (!session || session.division === false) {
    return router.back();
  } else {
    useEffect(() => {
      dispatch({ type: ADMIN_PRODUCTS_REQUEST, ShopId: session.Shops[0].id });
    }, []);
  }

  
  const { products } = useSelector((state) => state.shop);

  return (
    <>
      <List
        itemLayout={'horizontal'}
        dataSource={products}
        renderItem={(item) => {
          return (
            <List.Item key={item.id}>
              <Card
                style={{ width: '100%' }}
                actions={[
                  <Link href={`/shop/admin/product/edit/${item.id}`}>
                    <div>수정</div>
                  </Link>,
                  <div
                    onClick={() => {
                      console.log(item);
                      const flag = confirm(`이 제품을 정말로 삭제하시겠습니까?`);
                      if (!flag) {
                        return false;
                      } else {
                        dispatch({ type: DELETE_PRODUCT_REQUEST, id: item.id });
                      }
                    }}
                  >
                    삭제
                  </div>,
                ]}
              >
                <List.Item.Meta
                  avatar={
                    <Avatar
                      src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                      shape={'square'}
                      size={'large'}
                    />
                  }
                  title={item.title}
                  description={
                    <>
                      <p>
                        {item.price},{item.description}
                      </p>
                    </>
                  }
                />
              </Card>
            </List.Item>
          );
        }}
      />
    </>
  );
};

Console.protoType = {
  productList: PropTypes.object.isRequired,
};

export default Console;
