import ProductCard from "../components/ProductCard";
import { useSelector, useDispatch } from "react-redux";
import CentralAppLayout from "../components/CentralAppLayout";
import NavibarLayout from "../components/NavibarLayout";
import { ADD_QUANTITY_SUCCESS } from "../reducers/products";

const Index = () => {
  const { products } = useSelector((state) => state.products);

  return (
    <>
      <CentralAppLayout>
        <NavibarLayout>
          {products &&
            products.map((product) => {
              return (
                <ProductCard
                  key={product.id}
                  id={product.id}
                  title={product.title}
                  price={product.price}
                  quantity={product.quantity}
                  cover={product.cover}
                />
              );
            })}
        </NavibarLayout>
      </CentralAppLayout>
    </>
  );
};

export default Index;
