import { useState } from "react";
import { Form, Input, Button, Upload, PageHeader } from "antd";
import { useInput } from "../../../components/Generalui/CustomHooks";
import ImgCrop from "antd-img-crop";
import CentralAppLayout from "../../../components/Layout/CentralAppLayout";
import { InboxOutlined } from "@ant-design/icons";
import { useDispatch } from "react-redux";
import { ADD_PRODUCT_REQUEST } from "../../../reducers/shop";

const Regist = () => {
  const [name, onChangeName] = useInput();
  const [price, onChangePrice] = useInput();
  const [description, onChangeDecription] = useInput();
  const [fileList, setFileList] = useState([]);

  const dispatch = useDispatch();

  const onImagesChange = ({ fileList: newFileList }) => {
    setFileList(newFileList);
  };

  const onPreview = async (file) => {
    let src = file.url;
    if (!src) {
      src = await new Promise((resolve) => {
        const reader = new FileReader();
        reader.readAsDataURL(file.originFileObj);
        reader.onload = () => resolve(reader.result);
      });
    }
    const image = new Image();
    image.src = src;
    const imgWindow = window.open(src);
    imgWindow.document.write(image.outerHTML);
  };

  const onSubmit = (e) => {
    // console.log(`submit`);
    // e.preventDefault();
    // const formData = new FormData();
    // formData.append(`name`, name);
    // formData.append(`price`, price);
    // formData.append(`description`, description);
    // fileList.forEach((i) => formData.append(`image`, i));
    // dispatch({
    //   type: ADD_PRODUCT_REQUEST,
    //   formData,
    // });
    console.log(`submit`);
  };

  return (
    <CentralAppLayout>
      <PageHeader className="site-page-header" onBack={() => null} title="상품등록" />

      <Form encType={"multipartForm"} onSubmit={onSubmit}>
        <Input addonBefore={`name`} onChange={onChangeName} style={{ marginTop: 10 }} />
        <Input
          addonBefore={`price`}
          onChange={onChangePrice}
          type={`number`}
          style={{ marginTop: 10 }}
        />
        <Input addonBefore={`desc`} onChange={onChangeDecription} style={{ marginTop: 10 }} />

        <ImgCrop rotate aspect={4 / 3}>
          <Upload
            action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
            listType="picture-card"
            fileList={fileList}
            onChange={onImagesChange}
            onPreview={onPreview}
          >
            {fileList.length < 5 && "+ Upload"}
          </Upload>
        </ImgCrop>
        <Button type="primary" htmlType="submit">
          제출
        </Button>
      </Form>
    </CentralAppLayout>
  );
};

export default Regist;
