import { List, Avatar, Card, Button, Row, Col, Divider } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { RestOutlined } from "@ant-design/icons";
import CartAmountToggle from "../../components/Generalui/CartAmoutToggle";

const Cart = () => {
  const { cart } = useSelector((state) => state.cart);
  return (
    <>
      <List
        itemLayout="horizontal"
        dataSource={cart}
        renderItem={(item) => (
          <List.Item>
            <Card title={item.title} style={{ width: "100%" }} extra={<RestOutlined />}>
              <Card.Meta
                description={
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        size={100}
                        shape="square"
                        src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                      />
                    }
                    description={
                      <Row Gutter={[8, 16]}>
                        <Col span={24}>{item.description}</Col>
                        <Col span={20}>{item.price}</Col>
                        <Col span={4}>
                          <CartAmountToggle id={item.id} quantity={item.quantity} />
                        </Col>
                        <Divider></Divider>
                        <Col span={24}>{item.price * item.quantity}</Col>
                      </Row>
                    }
                  />
                }
              />
            </Card>
          </List.Item>
        )}
      />
    </>
  );
};

export default Cart;
