import { List, Avatar, Card, Button, Row, Col, Divider, Empty } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { RestOutlined } from '@ant-design/icons';
import CartAmountToggle from '../../components/Generalui/CartAmoutToggle';
import NumberFormat from 'react-number-format';
import { REMOVE_ITEM_REQUEST } from '../../reducers/cart';
import { PAYMENT_SUCCESS, PAYMENT_FAILURE } from '../../reducers/payment';
import { useState, useEffect } from 'react';
import { useWindowSize } from '@react-hook/window-size';
import axios from 'axios';
import { useRouter } from 'next/router';

const Cart = () => {
  const dispatch = useDispatch();
  const [IMP, setIMP] = useState(null);
  const { cart, ShopId, TableId } = useSelector((state) => state.cart);
  const { isLoggedIn, session } = useSelector((state) => state.user);
  const [windowWidth, windowHeight] = useWindowSize();
  const router = useRouter();

  const onWindowScroll = () => {
    window.scrollTo({ top: 0 });
  };

  const product = cart[0];

  let amount = 0;
  cart.map((product) => (amount += product.price * product.quantity));

  useEffect(() => {
    const { IMP } = window;
    setIMP(IMP);
  }, []);

  const onClickPayment = () => {
    onWindowScroll();
    const name = cart.length > 1 ? `${product.title} 외 ${cart.length - 1} 건` : product.title;

    if (isLoggedIn) {
      if (IMP !== null) {
        const purchaseDatas = cart.map((product) => {
          return {
            ProductId: product.id,
            ShopId: product.Shop.id,
            UserId: session.id,
            price: product.Discount
            ? product.price * (100 - item.Discount.rate) * 0.01
            : item.price,
            quantity: product.quantity,
            ShopName: product.Shop.name,
            ProductTitle: product.title,
          };
        });
        const cartJson = JSON.stringify(purchaseDatas);
        IMP.init('imp37098043');
        const today = Date.now();
        IMP.request_pay(
          {
            pay_method: 'card',
            name,
            Merchant_uid: 'uid' + today,
            amount,
            custom_data: cartJson,
          },
          callback,
        );
      }
    } else {
      alert('결제실패');
    }
  };

  const callback = async (response) => {
    const { success, error_msg } = response;
    if (success) {
      axios
        // .post(`${hostIp}/payments/complete/${product.id}/${product.Shop.id}/${session.id}`, { data: response })
        .post(`http://127.0.0.1:3065/payments/complete`, { data: response })
        .then((response) => {
          dispatch({ type: PAYMENT_SUCCESS }, { data: response });
          router.push(TableId === undefined ? `/menu/${ShopId}` : `/menu/${ShopId}/${TableId}`);
        })
        .catch((e) => {
          console.log(e);
          dispatch({ type: PAYMENT_FAILURE }, { error: e });
        });
    } else {
      alert(`결제 실패: ${error_msg}`);
    }
  };

  return (
    <>
      {cart.length === 0 ? (
        <Card style={{ height: '100vh' }}>
          <div style={{ marginTop: windowHeight * 0.3, textAlign: 'center' }}>
            <Empty />
            <h1>장바구니가 비었습니다</h1>
          </div>
        </Card>
      ) : (
        <List
          itemLayout="horizontal"
          dataSource={cart}
          renderItem={(item) => (
            <List.Item>
              <Card
                title={<h1>{item.title}</h1>}
                style={{ width: '100%' }}
                extra={
                  <h2>
                    <RestOutlined
                      onClick={() => {
                        dispatch({ type: REMOVE_ITEM_REQUEST, id: item.id });
                      }}
                    />
                  </h2>
                }
              >
                <Card.Meta
                  description={
                    <List.Item.Meta
                      avatar={
                        <Avatar
                          size={100}
                          shape="square"
                          src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`}
                        />
                      }
                      description={
                        <Row Gutter={[8, 16]}>
                          <Col span={24}>{item.description}</Col>
                          <Col span={16}>
                            가격:{' '}
                            <NumberFormat
                              value={
                                item.Discount
                                  ? item.price * (100 - item.Discount.rate) * 0.01
                                  : item.price
                              }
                              displayType={'text'}
                              thousandSeparator={true}
                            />
                          </Col>
                          <Col span={8}>
                            <CartAmountToggle id={item.id} quantity={item.quantity} />
                          </Col>
                        </Row>
                      }
                    />
                  }
                />
                <Divider />
                <Row gutter={[8, 16]}>
                  <Col style={{ textAlign: 'right' }} span={24}>
                    총액:{' '}
                    <NumberFormat
                      value={
                        item.Discount
                          ? item.price * (100 - item.Discount.rate) * 0.01 * item.quantity
                          : item.price * item.quantity
                      }
                      displayType={'text'}
                      thousandSeparator={true}
                    />
                  </Col>
                </Row>
              </Card>
            </List.Item>
          )}
        />
      )}
      {cart.length > 0 && (
        <Button onClick={onClickPayment} type={'primary'} style={{ width: '100%' }}>
          결제하기
        </Button>
      )}
    </>
  );
};

export default Cart;
