import { Menu, Input, Modal } from 'antd';
import Link from 'next/link';
import { ArrowLeftOutlined, UserOutlined } from '@ant-design/icons';
import { useSelector, useDispatch } from 'react-redux';
import Router from 'next/router';
import { LOG_OUT_SUCCESS } from '../../reducers/user';
import LoginModal from '../Generalui/Login';
import { useState } from 'react';
import { LOG_IN_ERROR_CLEAR } from '../../../../project2/frontend/reducers/user';

const NavibarLayout = () => {
  const [toggleLogin, setToggleLogin] = useState(false);
  const [toggleSearch, setToggleSearch] = useState(false);

  const dispatch = useDispatch();
  const onClickLogout = () => {
    dispatch({ type: LOG_OUT_SUCCESS });
  };

  const onChangeToggleLogin = () => {
    dispatch({ type: LOG_IN_ERROR_CLEAR });
    setToggleLogin((prev) => !prev);
  };

  const onChangeToggleSearch = () => {
    setToggleLogin((prev) => !prev);
  };

  const { isLoggedIn } = useSelector((state) => state.user);

  return (
    <>
      <Modal title={'Login'} visible={toggleLogin} onCancel={onChangeToggleLogin} footer={null}>
        <LoginModal setToggleLogin={setToggleLogin} />
      </Modal>
      <Modal  visible={toggleLogin} onCancel={onChangeToggleLogin} footer={null}>
        <LoginModal setToggleLogin={setToggleLogin} />
      </Modal>
      <div style={{ marginTop: '10px', backGroundColor: 'white' }}>
        <Menu mode="horizontal">
          <Menu.Item>
            <ArrowLeftOutlined onClick={() => Router.back()} />
          </Menu.Item>
          {!isLoggedIn
            ? [
                <Menu.Item style={{ float: 'right' }} key={'login'}>
                  <a onClick={onChangeToggleLogin}>로그인</a>
                </Menu.Item>,
              ]
            : [
                <Menu.Item style={{ float: 'right' }} key={'logout'}>
                  <a onClick={onClickLogout}>로그아웃</a>
                </Menu.Item>,
                <Menu.Item key={'profile'} style={{ float: 'right' }} key={'profile'}>
                  <Link href={'/user/profile'}>
                    <a>
                      <UserOutlined />
                    </a>
                  </Link>
                </Menu.Item>,
              ]}
          <Menu.Item key={'search'} style={{ float: 'right' }} key={'search'}>
            <Input.Search
              enterButton
              style={{ verticalAlign: 'middle' }}
              size={`large`}
              onClick={onChangeToggleSearch}
            />
          </Menu.Item>
        </Menu>
      </div>
    </>
  );
};

export default NavibarLayout;
