import { useState, useCallback } from "react";
import { Form, Input, Button, Upload, PageHeader, Space, Card } from "antd";
import { useInput } from "../../../components/Generalui/CustomHooks";
import ImgCrop from "antd-img-crop";
import { useDispatch } from "react-redux";
import { ADD_PRODUCT_REQUEST } from "../../../reducers/shop";
import { GiftOutlined, DollarOutlined, ProfileOutlined } from "@ant-design/icons";

const Regist = () => {
  const dispatch = useDispatch();

  const [title, onChangeTitle] = useInput();
  const [price, setPrice] = useState();
  const [description, onChangeDecription] = useInput();
  const [fileList, setFileList] = useState([]);

  const onChangePrice = (e) => {
    const regx = /[^0-9]/gi;
    const value = e.target.value.replace(regx, "");
    setPrice(value);
  };

  const onImagesChange = ({ fileList: newFileList }) => {
    setFileList(newFileList);
  };

  const onPreview = async (file) => {
    let src = file.url;
    if (!src) {
      src = await new Promise((resolve) => {
        const reader = new FileReader();
        reader.readAsDataURL(file.originFileObj);
        reader.onload = () => resolve(reader.result);
      });
    }
    const image = new Image();
    image.src = src;
    const imgWindow = window.open(src);
    imgWindow.document.write(image.outerHTML);
  };

  const onFinish = () => {
    const formData = new FormData();
    formData.append("title", title);
    formData.append("price", price);
    formData.append("description", description);
    fileList.forEach(({ originFileObj }) => {
      formData.append("files", originFileObj);
    });
    dispatch({
      type: ADD_PRODUCT_REQUEST,
      formData,
    });
  };

  return (
    <Form encType={"multipart/form-data"} onFinish={onFinish}>
      <Card
        title={<PageHeader className="site-page-header" title="상품등록" />}
        style={{ height: "100vh" }}
      >
        <Space direction="vertical" size={"middle"} style={{ width: "100%" }}>
          <Form.Item name="title" rules={[{ required: true, message: "상품명을 입력해주세요" }]}>
            <Input name="title" prefix={<GiftOutlined />} size={"large"} onChange={onChangeTitle} />
          </Form.Item>
          <Form.Item name="price" rules={[{ required: true, message: "가격을 입력해주세요" }, {type: 'number'}]}>
            <Input
              name="price"
              prefix={<DollarOutlined />}
              value={price}
              size={"large"}
              onChange={onChangePrice}
            />
          </Form.Item>
          <Form.Item
            name="description"
            rules={[{ required: true, message: "상세내역을 입력해주세요" }]}
          >
            <Input
              name="description"
              prefix={<ProfileOutlined />}
              size={"large"}
              onChange={onChangeDecription}
            />
          </Form.Item>
          <Card title={`image`}>
            <Form.Item
              name={"image"}
              rules={[{ required: true, message: "파일을 업로드 해주세요" }]}
            >
              <ImgCrop rotate aspect={4 / 3} name={"image"}>
                <Upload
                  action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                  listType="picture-card"
                  fileList={fileList}
                  onChange={onImagesChange}
                  onPreview={onPreview}
                >
                  {fileList.length < 5 && "+ Upload"}
                </Upload>
              </ImgCrop>
            </Form.Item>
          </Card>
          <Button type="primary" htmlType="submit" style={{ width: `100%` }}>
            제출
          </Button>
        </Space>
      </Card>
    </Form>
  );
};

export default Regist;
