import { List, Button, Avatar } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import PropTypes from "prop-types";
import { ADMIN_PRODUCTS_REQUEST } from "../../../reducers/shop";

const Console = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch({ type: ADMIN_PRODUCTS_REQUEST });
  }, []);
  const { productList } = useSelector((state) => state.shop);
  console.log(productList);
  return (
    <List
      dataSource={productList}
      renderItem={(item) => {
        console.log(item.title);
        return (
          <List.Item key={item.id}>
            <Avatar src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`} />
            <List.Item.Meta
              avatar={<Avatar src={`http://127.0.0.1:3065/uploads/${item.Images[0].url}`} />}
              title={item.title}
              description={item.description}
            />
          </List.Item>
        );
      }}
    />
  );
};
export default Console;
