import { List, Button, Avatar } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import PropTypes from "prop-types";
import { LOAD_PRODUCTS_REQUEST } from "../../../reducers/shop";

const Console = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch({ type: LOAD_PRODUCTS_REQUEST });
  }, []);
  const { products } = useSelector((state) => state.shop);
  console.log(products);
  return (
    <>
      <List
        dataSource={products}
        renderItem={(item) => (
          <List.Item key={item.key}>
            <List.Item.Meta
              avatar={<Avatar src={`/uploads/${item.image[0]}`} />}
              title={item.title}
              description={item.description}
            />
          </List.Item>
        )}
      ></List>
      <div>test</div>
    </>
  );
};

Console.propTypes = {};

Console.getInitialProps = async (context) => {
  const items = await context.store.dispatch({ type: LOAD_PRODUCTS_REQUEST });
  return items;
};

export default Console;
