import { useState, useEffect } from "react";
import { Button } from "antd";

const Import = () => {
  const [IMP, setIMP] = useState(null);

  useEffect(() => {
    const { IMP } = window;

    setIMP(IMP);
  }, []);
  const onClickPayment = () => {
    console.log("yes");
    alert("click!");
    // if (IMP !== null) {
    //   IMP.request_pay(
    //     {
    //       pg: "inicis", // version 1.1.0부터 지원.
    //       pay_method: "card",
