import axios from "axios";
import { all, put, fork, call, takeLatest } from "redux-saga/effects";
import {
  LOAD_PRODUCTS_REQUEST,
  LOAD_PRODUCTS_SUCCESS,
  LOAD_PRODUCTS_FAILURE,
  ADD_PRODUCT_REQUEST,
  ADD_PRODUCT_SUCCESS,
  ADD_PRODUCT_FAILURE,
} from "../reducers/shop";

function loadProductApi() {
  return axios.get(`/shop/${id}/${table}`);
}

function* loadProducts() {
  try {
    const result = yield call(loadProductApi);
    yield put({
      type: LOAD_PRODUCTS_SUCCESS,
      data: result.data,
    });
  } catch (e) {
    console.log(e);
    yield put({
      type: LOAD_PRODUCTS_FAILURE,
      error: e,
    });
  }
}

function* watchLoadProducts() {
  yield takeLatest(LOAD_PRODUCTS_REQUEST, loadProducts);
}

function addProductApi(data) {
  console.log(data);
  return axios.post(
    "/products/add",
    data,
    {
      header: { "Content-Type": "multipart/form-data" },
    },
    {
      withCredentials: true,
    }
  );
}

function* addProduct(action) {
  try {
    console.log(action.formData);
    const result = yield call(addProductApi, action.formData);
    yield put({ type: ADD_PRODUCT_SUCCESS, data: result.data });
  } catch (e) {
    console.log(e);
    yield put({ type: ADD_PRODUCT_FAILURE, error: e });
  }
}

function* watchAddProduct() {
  yield takeLatest(ADD_PRODUCT_REQUEST, addProduct);
}

export default function* productsSaga() {
  yield all([fork(watchLoadProducts), fork(watchAddProduct)]);
}
